  Clinic.init({ // viet nhung thuoc tinh cua Clinic vao day
    name: DataTypes.STRING,
    address: DataTypes.STRING,
    image: DataTypes.STRING,
    descriptionHTML: DataTypes.TEXT,
    descriptionMarkdown: DataTypes.TEXT,
  }, {
    sequelize,
    modelName: 'Clinic',
  });
  return Clinic;
};

