/* eslint-disable no-async-promise-executor */
import nodemailer from 'nodemailer';
import _ from 'lodash';
import moment from 'moment';
require('dotenv').config();

const contentMail = (data) => {
  let result = '';
  if (data.language === 'vi') {
    result = `
            <p>Xin cho, ${data.name}!</p>
            <p>Bn  t lch khm bnh thnh cng!</p>
            <p>Thng tin t lch khm bnh:</p>
            <div><b>Bc s: ${data.lastName} ${data.firstName}</b></div>
            <div><b>Thi gian: ${buildTimeBooking(data)}</b></div>
            <p>Vui lng xc click vo ng link bn di  xc nhn v hon tt th tc t lch khm bnh</p>
            <div>
            <a href=${data.verificationLink} target='_blank'>Click here</a>
            </div>

            <div>Xin chn thnh cm n!</div>
        `;
  }
  if (data.language === 'en') {
    result = `
            <p>Hello, ${data.name}!</p>
            <p>You have successfully booked a medical appointment!</p>
            <p>Information to book an appointment:</p>
            <div><b>Doctor: ${data.lastName} ${data.firstName}</b></div>
            <div><b>Time: ${buildTimeBooking(data)}</b></div>
            <p>Please click on the link below to confirm and complete the booking procedure</p>
            <div>
            <a href=${data.verificationLink} target='_blank'>Click here</a>
            </div>

            <div>Thank you very much!</div>
        `;
  }
  if (data.language === 'ru') {
    result = `
            <p>, ${data.name}!</p>
            <p>      !</p>
            <p>    :</p>
            <div><b>: ${data.lastName} ${data.firstName}</b></div>
            <div><b>: ${buildTimeBooking(data)}</b></div>
            <p>,    ,      </p>
            <div>
            <a href=${data.verificationLink} target='_blank'> </a>
            </div>

            <div> !</div>
        `;
  }
  return result;
};

const buildTimeBooking = (data) => {
  let date = '';
  let time = '';
  if (data.dataTime && !_.isEmpty(data.dataTime)) {
    if (data.language === 'vi') {
      date = moment.unix(+data.dataTime.date / 1000).format('dddd - DD/MM');
      time = data.dataTime.timeTypeData.valueVi;
    }
    if (data.language === 'en') {
      date = moment
        .unix(+data.dataTime.date / 1000)
        .locale('en')
        .format('dd - DD/MM');
      time = data.dataTime.timeTypeData.valueEn;
    }
    if (data.language === 'ru') {
      date = moment
        .unix(+data.dataTime.date / 1000)
        .locale('ru')
        .format('dd - DD/MM');
      time = data.dataTime.timeTypeData.valueRu;
    }
  }
  time = `${time} - ${date}`;
  return time;
};

const sendMail = async (data) => {
  const transporter = nodemailer.createTransport({
    host: 'smtp.gmail.com',
    port: 587,
    secure: false, // true for 465, false for other ports
    auth: {
      user: process.env.APP_GMAIL, // generated ethereal user
      pass: process.env.APP_GMAIL_PASSWORD // generated ethereal password
    }
  });

  // send mail with defined transport object
  // eslint-disable-next-line no-unused-vars
  const info = await transporter.sendMail({
    from: '"no-reply" <hoangk24aas@gmail.com>', // sender address
    to: data.email, // list of receivers
    subject: 'Online Appointment Booking', // Subject line
    html: contentMail(data) // html body
  });
};

const sendAttachment = (dataSend) => {
  return new Promise(async (resolve, reject) => {
    try {
      const transporter = nodemailer.createTransport({
        host: 'smtp.gmail.com',
        port: 587,
        secure: false, // true for 465, false for other ports
        auth: {
          user: process.env.APP_GMAIL, // generated ethereal user
          pass: process.env.APP_GMAIL_PASSWORD // generated ethereal password
        }
      });

      // send mail with defined transport object
      // eslint-disable-next-line no-unused-vars
      const info = await transporter.sendMail({
        from: '"no-reply" <hoangk24aas@gmail.com>', // sender address
        to: dataSend.email, // list of receivers
        subject: 'Result of appointment booking', // Subject line
        html: contentMailRemedy(dataSend), // html body
        attachments: [
          {
            filename: `Remedy-${dataSend.namePatient}-doctor-${dataSend.lastNameDoctor}-${dataSend.firstNameDoctor}.png`,
            content: dataSend.imageBase64.split('base64,')[1],
            encoding: 'base64'
          }
        ]
      });
      resolve({
        errCode: 0,
        errMessage: 'Send attachment successfully'
      });
    } catch (e) {
      console.log(e);
      reject(e);
    }
  });
};

const contentMailRemedy = (data) => {
  let result = '';
  if (data.language === 'vi') {
    result = `
            <p>Xin cho, ${data.namePatient}!</p>
            <p>Bn nhn c email v  khm thnh cng!</p>
            <div><b>Bc s: ${data.lastNameDoctor} ${data.firstNameDoctor}</b></div>
            <p>Thng tin n thuc/ha n c bc s gi trong file nh km</p>
            <div>Xin chn thnh cm n!</div>
        `;
  }
  if (data.language === 'en') {
    result = `
            <p>Hello, ${data.namePatient}!</p>
            <p>You received an email because your scan was successful!</p>
            <div><b>Doctor: ${data.lastNameDoctor} ${data.firstNameDoctor}</b></div>
            <p>Prescription/invoice information sent by the doctor in the attached file</p>
            <div>Thank you very much!</div>
        `;
  }
  if (data.language === 'ru') {
    result = `
            <p>, ${data.namePatient}!</p>
            <p>   ,    !</p>
            <div><b>Bc s: ${data.lastNameDoctor} ${data.firstNameDoctor}</b></div>
            <p>  /,     </p>
            <div> !</div>
        `;
  }
  return result;
};

module.exports = {
  sendMail: sendMail,
  sendAttachment: sendAttachment
};

